export function OptimizeSvg(inputFileUrl, callback) {
    var request = new XMLHttpRequest();
    request.open("GET", inputFileUrl);
    request.setRequestHeader("Content-Type", "image/svg+xml");
    request.addEventListener("load", function(event) {
        var response = event.target.responseText;
        var doc = new DOMParser();
        var xml = doc.parseFromString(response, "image/svg+xml");
        [...xml.getElementsByTagName("path")].forEach(path => {
            PathSplitter(path.getAttribute("d")).forEach(pathDataPart => xml.insertBefore(path, path.cloneNode(true).setAttribute("d", pathDataPart)));
            xml.removeChild(path);
        });
        var svgData = new XMLSerializer().serializeToString(xml);
        callback(svgData)
    });
    return request.send();
}
