import "./Images.css"
import React, {useState, useRef} from "react";

export default function Images() {
    const fileInputField = useRef(null);
    const [file, setFile] = useState(null);

    const handleNewFileUpload = (e) => {
        const { files: newFiles } = e.target;
        if (newFiles.length === 1) {
          setFile(newFiles[0]);
        }
    };

    const handleUploadAreaClick = () => {
        fileInputField.current.click();
      };

    const imageFileUploaded = file && file.type.split("/")[0] === "image";

    return (
        <div className="Images">

            {imageFileUploaded ? 
                <div className="Images-input">
                    <img src={URL.createObjectURL(file)} alt="input svg"></img>
                </div>
                : 
                <div className="Images-input file-upload-area" onClick={handleUploadAreaClick}>
                    <input style={{display: "none"}} type="file" ref={fileInputField} onChange={handleNewFileUpload} />
                    <header>
                        Upload SVG file
                    </header>
                </div>
            }
            <div className="Images-output">
                {imageFileUploaded && <img src={URL.createObjectURL(file)} alt="input svg"></img>}
            </div>
        </div>
    );
}
