import "./Images.css"
import React, {useState, useRef} from "react";
import uploadIcon from "./upload-solid.svg"

export default function Images() {
    const fileInputField = useRef(null);
    const [inputFile, setInputFile] = useState(null);
    const [outputFile, setOutputFile] = useState(null);

    const handleNewFileUpload = (e) => {
        const { files } = e.target;
        if (files.length === 1) {
            setInputFile(files[0]);
        }
    };

    const handleUploadClick = () => {
        fileInputField.current.click();
    };

    const didUploadSvgFile = inputFile && inputFile.type.split("/")[0] === "image" && inputFile.type.split("/")[1].indexOf("svg") !== -1;

    const preventDefaultFn = (e) => {
        e.preventDefault();
    }

    const fileDrop = (e) => {
        e.preventDefault();
        const files = e.dataTransfer.files;
        if (files.length === 1) {
            setInputFile(files[0]);
        }
    }

    return (
        <div className="Images">
            <input style={{display: "none"}} type="file" ref={fileInputField} onChange={handleNewFileUpload} />
            {didUploadSvgFile ? 
                <div className="Images-input">
                    <img src={URL.createObjectURL(inputFile)} alt="input svg"></img>
                    <img src={uploadIcon} className="upload-button" onClick={handleUploadClick} alt="upload file" width="40" height="40"></img>
                </div>
                : 
                <div 
                    className="file-upload-area" 
                    onClick={handleUploadClick}
                    onDragOver={preventDefaultFn}
                    onDragEnter={preventDefaultFn}
                    onDragLeave={preventDefaultFn}
                    onDrop={fileDrop}
                >
                    <label>
                        Drag and drop your files
                    </label>
                    <br/>
                    <label>
                        OR
                    </label>
                    <br/>
                    <label>
                        Click anywhere to choose from files
                    </label>
                </div>
            }
            {outputFile && <div className="Images-output">
                <img src={URL.createObjectURL(outputFile)} alt="input svg"></img>
            </div>}
        </div>
    );
}
