import "./Images.css"
import React, {useState, useRef} from "react";
import uploadIcon from "./upload-solid.svg"

export default function Images() {
    const fileInputField = useRef(null);
    const [file, setFile] = useState(null);

    const handleNewFileUpload = (e) => {
        const { files: newFiles } = e.target;
        if (newFiles.length === 1) {
          setFile(newFiles[0]);
        }
    };

    const handleUploadClick = () => {
        fileInputField.current.click();
      };

    const svgFileUploaded = file && file.type.split("/")[0] === "image" && file.type.split("/")[1].indexOf("svg") !== -1;

    return (
        <div className="Images">
            <input style={{display: "none"}} type="file" ref={fileInputField} onChange={handleNewFileUpload} />
            {svgFileUploaded ? 
                <div className="Images-input">
                    <img src={URL.createObjectURL(file)} alt="input svg"></img>
                    <img src={uploadIcon} className="upload-button" onClick={handleUploadClick} alt="upload file" width="40" height="40"></img>
                </div>
                : 
                <div className="Images-input file-upload-area" onClick={handleUploadClick}>
                    <header>
                        Upload SVG file
                    </header>
                </div>
            }
            {svgFileUploaded && <div className="Images-output">
                <img src={URL.createObjectURL(file)} alt="input svg"></img>
            </div>}
        </div>
    );
}
